mapper.update:
update `mail`
set context = #{context,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP},
  name = #{name,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, context, date, name, status
from `mail`
where id = #{id,jdbcType=INTEGER}
